import React, { useState } from 'react';
import './appform.css'; // This file will contain the necessary CSS
import Footer from './footer';
import { MapContainer, TileLayer, Marker, Popup, useMapEvents } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import Modal from 'react-modal';
import L from 'leaflet';


const icon = L.icon({
    iconUrl: 'src/to/pin.jpg', // Provide your custom marker icon path here
    iconSize: [25, 41],
    iconAnchor: [12, 41],
    popupAnchor: [0, -41],
});

const LocationPin = ({ onLocationSelect }) => {
    useMapEvents({
        click(e) {
            const { lat, lng } = e?.latlng || {};
            if (lat !== undefined && lng !== undefined) {
                onLocationSelect([lat, lng]);
            } else {
                console.error("Error: Location data is undefined.");
            }
        }
    });
    return null;
};
const Appform = () => {

    const [isMapVisible, setIsMapVisible] = useState(false);
    const [location, setLocation] = useState(null); // To store selected location
    const [formData, setFormData] = useState({
        loanId: '',
        applicantName: '',
        newRe: '',
        emailAddress: '',
        permanentAddress: '',
        presentAddress: '',
        telMob: '',
        sex: '',
        age: '',
        branch: '',
        civilStatus: '',
        spouseName: '',
        spouseOccu: '',
        location: {
            latitude: '',
            longitude: ''
        },
        loanType: '',
        loanAmount: '',
        interestRate: '',
        loanTerm: '',
        purposeLoan: '',
        employer: '',
        empCon: '',
        empStatus: '',
        businessName: '',
        businessAdd: '',
        lengthMem: '',
        shareCapital: '',
        savingsDepo: '',
        otherDepo: '',
        collateral: '',
        collateralOther: '',
        sourcePay: '',
        sourcePayOther: '',
        modePay: '',
        mannerPay: '',
        memberSig: '',
        spouseSig: '',
        repaymentSchedule: '',
        disbursementDate: '',
        currentBalance: '',
        paymentDueDate: '',
        paymentStatus: '',
        defaultStatus: '',
        riskRating: '',
        applicationDate: '',
        approvalDate: '',
        notes: ''
    });

    const handleFileChange = (e) => {
        const { name, files } = e.target;
        const file = files[0];
        
        if (file) {
            const reader = new FileReader();
            reader.onloadend = () => {
                setFormData((prevData) => ({
                    ...prevData,
                    [name]: reader.result // Store base64 or file data as needed
                }));
            };
            reader.readAsDataURL(file);
        }
    };

    const handleCheckboxChange = (event) => {
        const { name, value, checked } = event.target;
    
        // Handle the "applicationType" checkboxes
    if (name === 'applicationType') {
        setFormData((prevData) => ({
            ...prevData,
            newRe: checked ? value : '' // Set `newRe` based on the selected checkbox
        }));
    }

        // Check if the checkbox belongs to the "collateral" section
        if (name === 'collateral') {
            setFormData((prevData) => {
                const updatedCollateral = checked
                    ? [...prevData.collateral, value]
                    : prevData.collateral.filter((item) => item !== value);
    
                return {
                    ...prevData,
                    collateral: updatedCollateral,
                };
            });
        }
        // Check if the checkbox belongs to the "sourcePay" (Source of Payment) section
        else if (name === 'payment' && value !== 'others') {
            setFormData((prevData) => {
                const updatedPayment = checked
                    ? [...prevData.sourcePay, value]
                    : prevData.sourcePay.filter((item) => item !== value);
    
                return {
                    ...prevData,
                    sourcePay: updatedPayment,
                };
            });
        }
        // Handle the "others" checkbox specifically for "sourcePay"
        else if (name === 'payment' && value === 'others') {
            setFormData((prevData) => ({
                ...prevData,
                sourcePayOther: checked ? prevData.sourcePayOther : '', // Clear the text area if unchecked
            }));
        }
        // Handle the "mode of payment" checkboxes
        else if (name === 'payment' && (value === 'daily' || value === 'weekly' || value === 'semiMonthly' || value === 'monthly' || value === 'quarterly')) {
            setFormData((prevData) => {
                const updatedModePay = checked
                    ? [...prevData.modePay, value]
                    : prevData.modePay.filter((item) => item !== value);
    
                return {
                    ...prevData,
                    modePay: updatedModePay,
                };
            });
        }
        // Handle the "manner of payment" checkboxes
        else if (name === 'payment' && (value === 'otc' || value === 'collector' || value === 'payroll' || value === 'pdc' || value === 'debit')) {
            setFormData((prevData) => {
                const updatedMannerPay = checked
                    ? [...prevData.mannerPay, value]
                    : prevData.mannerPay.filter((item) => item !== value);
    
                return {
                    ...prevData,
                    mannerPay: updatedMannerPay,
                };
            });
        }
    };
    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };
    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('/loan', formData);
            alert('Loan submitted successfully');
            console.log(response.data);
        } catch (error) {
            console.error(error);
            alert('Error submitting loan');
        }
    };
    const handleLocationSelect = (selectedLocation) => {
        setLocation(selectedLocation); // Store selected location
        setIsMapVisible(false); // Optionally close the map after selection
    };
    const showMap = () => {
        setIsMapVisible(true); // show map when called
    };
    return (
        <div className="application-form-pageapp">
            {/* Header Section */}
            <header className="header">
                <img src="logo.png" alt="MSU-IIT NMPC Logo" className="logol" />
                <h2 className="landingh2off2">MSU-IIT National Multi-Purpose Cooperative</h2>
            </header>

            {/* Form Container */}
            <div className="sulodPorm">
            <h3 className="lontaytel">Loan Application Form</h3>
                <form className="porm">
                    <div className="plesDet">
                    <div className="lugar"> 
                        <label htmlFor="branch">BRANCH</label>
                        <select 
                            id="branch" 
                            name="branch" 
                            required 
                            value={formData.branch} // Access from formData
                            onChange={handleChange}  // Update formData on change
                        >
                            <option value="Tibanga-Main">Tibanga-Main</option>
                            <option value="Pala-o">Pala-o</option>
                            <option value="Buru-un">Buru-un</option>
                            <option value="Kiwalan">Kiwalan</option>
                            <option value="Poblacion">Poblacion</option>
                            <option value="Suarez-Tominobo">Suarez-Tominobo</option>
                            <option value="Tubod Iligan">Tubod Iligan</option>
                        </select>
                    </div>

                        <div className="det">
                            <label htmlFor="dateFiled">DATE FILED</label>
                            <input
                                type="date"
                                id="applicationDate"
                                name="applicationDate"
                                required
                                className="date-input"
                                value={formData.applicationDate} // Access from formData
                                onChange={handleChange}
                            />
                        </div>

                    </div>
                    
                    <div className="bago"> 
                        <div className="application-type">
                            <label className="pili">New
                                <input 
                                    type="checkbox" 
                                    id="payroll"  
                                    name="applicationType" 
                                    value="new" 
                                    checked={formData.newRe === 'new'} // Ensures only one is checked
                                    onChange={handleCheckboxChange} 
                                />
                                <span className="checkmarkapp"></span>
                            </label>
                            <label className="pili">Renewal
                                <input 
                                    type="checkbox" 
                                    id="renewal" 
                                    name="applicationType" 
                                    value="renewal" 
                                    checked={formData.newRe === 'renewal'} // Ensures only one is checked
                                    onChange={handleCheckboxChange} 
                                />
                                <span className="checkmarkapp"></span>
                            </label>
                        </div>
                    </div>


                    <div className="isa">

                        <div className="pormleft">
                            <div className="fillupan">
                                <label htmlFor="name">Name:</label>
                                <input type="text" id="applicantName" name="applicantName" value={formData.applicantName} onChange={handleChange} required />
                            </div>

                            <div className="fillupan">
                                <label htmlFor="email">Email Address:</label>
                                <input type="text" id="emailAddress" name="emailAddress" value={formData.emailAddress} onChange={handleChange} required />
                            </div>

                            <div className="fillupan">
                                <label htmlFor="permanentAddress">Permanent Address:</label>
                                <textarea id="permanentAddress" name="permanentAddress" value={formData.permanentAddress} onChange={handleChange} required></textarea>
                            </div>

                            <div className="fillupan">
                                <label htmlFor="presentAddress">Present Address:</label>
                                <textarea id="presentAddress" name="presentAddress" value={formData.presentAddress} onChange={handleChange} required></textarea>
                            </div>

                            <div className="fillupan">
                                <label htmlFor="phone">Telephone/Mobile No.:</label>
                                <input type="text" id="telMob" name="telMob" value={formData.telMob} onChange={handleChange} required />
                            </div>
                            <div className="ageapp">
                                <label htmlFor="age">Age:</label>
                                <input type="text" id="age" name="age" value={formData.age} onChange={handleChange} required />
                            </div>
                            <div className="genderapp">
                                <label htmlFor="sex">Sex:</label>
                                <select 
                                    id="sex" 
                                    name="sex" 
                                    required 
                                    value={formData.sex} // Access value from formData
                                    onChange={handleChange} // Update formData on selection change
                                >
                                    <option value="female">Female</option>
                                    <option value="male">Male</option>
                                </select>
                            </div>

                            <div className="civilapp">
                                <label htmlFor="civilStatus">Civil Status:</label>
                                <select id="civilStatus" name="civilStatus" required>
                                    <option value="single">Single</option>
                                    <option value="married">Married</option>
                                    <option value="widowed">Widowed</option>
                                </select>
                            </div>

                            <div className="fillupan">
                                <label htmlFor="spouseName">Name of Spouse:</label>
                                <input type="text" id="spouseName" name="spouseName" value={formData.spouseName} onChange={handleChange} required />
                            </div>

                            <div className="fillupan">
                                <label htmlFor="spouseOccupation">Spouse Occupation:</label>
                                <input type="text" id="spouseOccu" name="spouseOccu" value={formData.spouseOccu} onChange={handleChange} required />
                            </div>
                            
                                    <div className="form-row-checkbox-upload-date">
                                            <div className="checkboxq-groupapp">
                                                <label class="pili">Share Residence Location</label>
                                                </div>
                                                <div className="sher">
                                                    <textarea
                                                        onClick={showMap} // show map when clicking the textarea
                                                        type="text"
                                                        value={location ? `Latitude: ${location[0]}, Longitude: ${location[1]}` : ''}
                                                        placeholder="Please select your location on the map"
                                                        readOnly
                                                        className="location-input"
                                                    />
                                                    {isMapVisible && (
                                                        <div className="map">
                                                            {/* Map component or code would go here. Call handleMapClick(lat, lng) when location is chosen */}
                                                            <p>Select a location on the map.</p>
                                                        </div>
                                                    )}
                                                </div>

                                                

                                            {/* Modal for Map with Pin */}
                                            <Modal
                                                isOpen={isMapVisible}
                                                onRequestClose={() => setIsMapVisible(false)}
                                                contentLabel="Select Residence Location"
                                                className="map-modal"
                                                overlayClassName="map-modal-overlay"
                                            >
                                                <MapContainer
                                                    center={[8.2265, 124.2497]} 
                                                    zoom={13}
                                                    style={{ height: "500px", width: "100%" }}
                                                >
                                                    <TileLayer
                                                        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                                                        attribution="&copy; <a href='https://www.openstreetmap.org/copyright'>OpenStreetMap</a> contributors"
                                                    />
                                                    {/* Pin marker when user clicks */}
                                                    {location && (
                                                        <Marker position={location} icon={icon}>
                                                            <Popup>
                                                                Selected Location: <br /> Latitude: {location[0]} <br /> Longitude: {location[1]}
                                                            </Popup>
                                                        </Marker>
                                                    )}
                                                    <LocationPin onLocationSelect={handleLocationSelect} />
                                                </MapContainer>
                                            </Modal>
                                        </div>
                        </div>


                        <div className="pormrayt">
                        <div className="fillupan">
                            <label htmlFor="loanType">Loan Type:</label>
                            <select 
                                id="loanType" 
                                name="loanType" 
                                required 
                                value={formData.loanType} // Access value from formData
                                onChange={handleChange} // Update formData on selection change
                            >
                                <option value="personal">Personal Loan</option>
                                <option value="educational">Educational Loan</option>
                                <option value="pensioner">Pensioner Loan</option>
                            </select>
                        </div>


                            <div className="fillupan">
                                <label htmlFor="amountApplied">Amount Applied:</label>
                                <input type="text" id="loanAmount" name="loanAmount" value={formData.loanAmount} onChange={handleChange} required />
                            </div>

                            <div className="fillupan">
                                <label htmlFor="loanTerm">Term of Loan:</label>
                                <input type="text" id="loanTerm" name="loanTerm" value={formData.loanTerm} onChange={handleChange} required />


                            </div>

                            <div className="fillupan">
                                <label htmlFor="loanPurpose">Purpose of Loan:</label>
                                <textarea id="purposeLoan" name="purposeLoan" value={formData.purposeLoan} onChange={handleChange} required></textarea>
                            </div>

                            <div className="fillupan">
                                <label htmlFor="employer">Employer:</label>
                                <input type="text" id="employer" name="employer" value={formData.employer} onChange={handleChange} required />
                            </div>

                            <div className="fillupan">
                                <label htmlFor="employerContact">Employer Contact No.:</label>
                                <input type="text" id="empCon" name="empCon" value={formData.empCon} onChange={handleChange} required />
                            </div>

                            <div className="fillupan">
                                <label htmlFor="employmentStatus">Employment Status/Position Held:</label>
                                <input type="text" id="empStatus" name="empStatus" value={formData.empStatus} onChange={handleChange} required />
                            </div>

                            <div className="fillupan">
                                <label htmlFor="businessName">Business Name:</label>
                                <input type="text" id="businessName" name="businessName" value={formData.businessName} onChange={handleChange} required />
                            </div>

                            <div className="fillupan">
                                <label htmlFor="businessAddress">Business Address:</label>
                                <input type="text" id="businessAdd" name="businessAdd" value={formData.businessAdd} onChange={handleChange} required />
                            </div>

                            <div className="fillupan">
                                <label htmlFor="coopMembershipLength">Length of Coop Membership:</label>
                                <input type="text" id="lengthMem" name="lengthMem" value={formData.lengthMem} onChange={handleChange} required />
                            </div>

                            <div className="fillupan">
                                <label>Account Balance:</label>
                                <div className="balanceFields">
                                    <label htmlFor="shareCapital">Share Capital:</label>
                                    <input type="text" id="shareCapital" name="shareCapital" value={formData.shareCapital} onChange={handleChange} required />
                                    <label htmlFor="savingDeposit">Savings Deposit:</label>
                                    <input type="text" id="savingsDepo" name="savingsDepo" value={formData.savingsDepo} onChange={handleChange} required />
                                    <label htmlFor="otherDeposit">Other Deposit:</label>
                                    <input type="text" id="otherDepo" name="otherDepo" value={formData.otherDepo} onChange={handleChange} required />
                                </div>
                            </div>
                        </div>
                    </div>

                   
            <div className="form-rowapp">
                
                <div class="column-1app">
                
                <div className="fillupan">
                    <label htmlFor="payment">Collateral/Security Offered</label>
                    <div className="checkbox-groupapp">
                        <label className="pili">Real Estate - REM
                            <input type="checkbox" name="collateral" value="real-estate" onChange={handleCheckboxChange} />
                            <span className="checkmarkapp"></span>
                        </label>

                        <label className="pili">Vehicle - Chattel
                            <input type="checkbox" name="collateral" value="vehicle" onChange={handleCheckboxChange} />
                            <span className="checkmarkapp"></span>
                        </label>

                        <label className="pili">ATM Deposit
                            <input type="checkbox" name="collateral" value="atm-deposit" onChange={handleCheckboxChange} />
                            <span className="checkmarkapp"></span>
                        </label>

                        <label className="pili">Savings/Time Deposit
                            <input type="checkbox" name="collateral" value="savings-deposit" onChange={handleCheckboxChange} />
                            <span className="checkmarkapp"></span>
                        </label>

                        <label className="pili">Appliances
                            <input type="checkbox" name="collateral" value="appliances" onChange={handleCheckboxChange} />
                            <span className="checkmarkapp"></span>
                        </label>

                        <label className="pili">None
                            <input type="checkbox" name="collateral" value="none" onChange={handleCheckboxChange} />
                            <span className="checkmarkapp"></span>
                        </label>

                        <label className="pili">Others:
                            <input type="checkbox" name="collateral" value="others" onChange={handleCheckboxChange} />
                            <span className="checkmarkapp"></span>
                        </label>

                        {formData.collateral.includes('others') && (
                            <textarea
                                id="collateralOther"
                                name="collateralOther"
                                required
                                value={formData.collateralOther}
                                onChange={(e) => setFormData({ ...formData, collateralOther: e.target.value })}
                            />
                        )}
                    </div>
                </div>
                </div>

                {/* Source of Payment */}
                <div class="column-1app">
                <div class="fillupan">
                    <label for="payment">Source of Payment</label>
                    <div class="checkbox-groupapp">
                        <label class="pili">Salary
                            <input type="checkbox" id="salary" name="payment" value="salary" onChange={handleCheckboxChange} />
                            <span class="checkmarkapp"></span>
                        </label>

                        <label class="pili">Pension
                            <input type="checkbox" id="pension" name="payment" value="pension" onChange={handleCheckboxChange} />
                            <span class="checkmarkapp"></span>
                        </label>

                        <label class="pili">Allotment
                            <input type="checkbox" id="allotment" name="payment" value="allotment" onChange={handleCheckboxChange} />
                            <span class="checkmarkapp"></span>
                        </label>

                        <label class="pili">Commission/Incentives
                            <input type="checkbox" id="commission" name="payment" value="commission" onChange={handleCheckboxChange} />
                            <span class="checkmarkapp"></span>
                        </label>

                        <label class="pili">Income from Business
                            <input type="checkbox" id="income-business" name="payment" value="income-business" onChange={handleCheckboxChange} />
                            <span class="checkmarkapp"></span>
                        </label>

                        <label class="pili">Financial Assistance
                            <input type="checkbox" id="financial-assistance" name="payment" value="financial-assistance" onChange={handleCheckboxChange} />
                            <span class="checkmarkapp"></span>
                        </label>

                        <label class="pili">Others:
                            <input type="checkbox" id="payment-others" name="payment" value="others" onChange={handleCheckboxChange} />
                            <span class="checkmarkapp"></span>
                        </label>

                        {formData.sourcePay.includes('others') && (
                            <textarea
                                id="paymentOther"
                                name="paymentOther"
                                required
                                value={formData.sourcePayOther}
                                onChange={(e) => setFormData({ ...formData, sourcePayOther: e.target.value })}
                            />
                        )}
                    </div>
                </div>

                </div>

                <div class="column-1app">
                <div class="fillupan">
                    <label for="payment">Mode of Payment</label>
                    <div class="checkbox-groupapp">
                        <label class="pili">Daily
                            <input type="checkbox" id="daily" name="payment" value="daily" onChange={handleCheckboxChange} />
                            <span class="checkmarkapp"></span>
                        </label>

                        <label class="pili">Weekly
                            <input type="checkbox" id="weekly" name="payment" value="weekly" onChange={handleCheckboxChange} />
                            <span class="checkmarkapp"></span>
                        </label>

                        <label class="pili">Semi-Monthly
                            <input type="checkbox" id="semiMonthly" name="payment" value="semiMonthly" onChange={handleCheckboxChange} />
                            <span class="checkmarkapp"></span>
                        </label>

                        <label class="pili">Monthly
                            <input type="checkbox" id="monthly" name="payment" value="monthly" onChange={handleCheckboxChange} />
                            <span class="checkmarkapp"></span>
                        </label>

                        <label class="pili">Quarterly
                            <input type="checkbox" id="quarterly" name="payment" value="quarterly" onChange={handleCheckboxChange} />
                            <span class="checkmarkapp"></span>
                        </label>
                    </div>
                </div>

                </div>

                <div class="column-1app">
                    <div class="fillupan">
                        <label for="payment">Manner of Payment</label>
                        <div class="checkbox-groupapp">
                            <label class="pili">Thru Coop/OTC
                                <input type="checkbox" id="otc" name="payment" value="otc"/>
                                <span class="checkmarkapp"></span>
                            </label>

                            <label class="pili">Collector
                                <input type="checkbox" id="collector" name="payment" value="collector"/>
                                <span class="checkmarkapp"></span>
                            </label>

                            <label class="pili">Payroll Deduction
                                <input type="checkbox" id="payroll" name="payment" value="payroll"/>
                                <span class="checkmarkapp"></span>
                            </label>

                            <label class="pili">PDC
                                <input type="checkbox" id="pdc" name="payment" value="pdc"/>
                                <span class="checkmarkapp"></span>
                            </label>

                            <label class="pili">Auto-Debit
                                <input type="checkbox" id="debit" name="payment" value="debit"/>
                                <span class="checkmarkapp"></span>
                            </label>
                        </div>
                    </div>
                </div>
                </div>


                            <p className="aboutp">
                                I warrant the truth and correctness of all data 
                                and information herein to the best of my knowledge. 
                                I expressly submit to any credit investigation and hereby agree that 
                                any false information that will be discovered will automatically cause
                                the disapproval of this application.
                            </p>
                            <br></br>
                            {/* Upload Signature and Date */}
                            <div className="form-rowapp">
                                <div className="columnapp">
                                    <div className="form-groupapp">
                                        <label htmlFor="borrower-signature">Member-Borrowers Signature</label>
                                        <input type="file" id="borrower-signature" name="memberSig" onChange={handleFileChange} required />
                                    </div>
                                </div>

                                <div className="columnapp">
                                    <div className="form-groupapp">
                                        <label htmlFor="spouse-signature">Spouses Signature</label>
                                        <input type="file" id="spouse-signature" name="spouseSig" onChange={handleFileChange} />
                                    </div>
                                </div>

                            </div>

                           

                            <div class="parent-containerapp">
                                <a href="/cashflow" class="submit-btn">Next </a>
                            </div>


                        </form>
                    </div>

         <Footer />
        </div>
    );
};

export default Appform;
